mapper.select:
select num, id, subject, content,to_char(w_day,'yyyy-mm-dd') w_day from (select rownum r, num, id, subject, content, w_day from (select num, id, subject, content, w_day from notice
where ${ field } like '%'||#{ keyword }||'%'
order by num desc)) where r between #{ startNum } and #{ endNum }

select num,subject,content,to_char(w_day,'yyyy-mm-dd') w_day
from notice
where num = #{num}

mapper.update:
update notice
set subject=#{subject} , content=#{content}
where num=#{num}
